CREATE TABLE IF NOT EXISTS Chef(chefid INT PRIMARY KEY, name VARCHAR(100), bio TEXT, shortDesc VARCHAR(50), location BLOB, rating FLOAT, numReviews INT, profilePic TEXT); 
CREATE TABLE IF NOT EXISTS Dish(dishid INT PRIMARY KEY, chefid INT, name VARCHAR(100), price FLOAT, description TEXT, shortDesc VARCHAR(100), ingredients TEXT, timeMin INT, rating FLOAT, numReviews INT, primaryImage TEXT, secondImage TEXT, thirdImage TEXT, fourthImage TEXT, available BOOLEAN); 
CREATE TABLE IF NOT EXISTS Orders(orderid INT PRIMARY KEY, customer VARCHAR(100), dishid INT, chefid INT, placed DATETIME);
CREATE TABLE IF NOT EXISTS Review(dishid INT, chefid INT, reviewer VARCHAR(100), rating INT, comment TEXT, timestamp DATETIME, PRIMARY KEY (dishid, reviewer, timestamp)); 