CREATE TABLE IF NOT EXISTS Chef(chefid INT PRIMARY KEY, name VARCHAR(100), bio TEXT, shortDesc VARCHAR(50), location TEXT, rating FLOAT, numReviews INT, profilePic TEXT); 
CREATE TABLE IF NOT EXISTS Dish(dishid INT PRIMARY KEY, chefid INT, name VARCHAR(100), price FLOAT, description TEXT, shortDesc VARCHAR(100), ingredients TEXT, timeMin INT, rating FLOAT, category VARCHAR(100), numReviews INT, primaryImage TEXT, secondImage TEXT, thirdImage TEXT, fourthImage TEXT, available BOOLEAN); 
CREATE TABLE IF NOT EXISTS Orders(orderid INT PRIMARY KEY, customer VARCHAR(100), dishid INT, chefid INT, placed DATETIME);
CREATE TABLE IF NOT EXISTS Review(dishid INT NOT NULL, chefid INT NOT NULL, reviewer VARCHAR(100) NOT NULL, rating INT NOT NULL, comment TEXT, timestamp BIGINT NOT NULL, PRIMARY KEY (dishid, reviewer, timestamp)); 
